
public class Assignment1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		int b = 20;
		int temp = 30;
		
		System.out.println("before swapping");
		System.out.println(a);
		System.out.println(b);
		
		//swapping
		temp = a;
		a = b;
		b = temp;
		
		System.out.println("After swapping");
		System.out.println(a);
		System.out.println(b);
	}

}
