
// Swap three number without temp variable
public class Assignment4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		int b = 20;
		int c = 30;
		
		//swap
		
		 a=a+b+c;
		 
		 b=a-b-c;
		 
		 c=a-b-c;
		 
		 a=a-b-c;

		
		
		
		System.out.println(a);
		System.out.println(b);
		System.out.println(c);
	
			
	}

}
